import React from 'react';
import { Container, Typography, Button, Grid } from '@material-ui/core';
import useStyles from "./styles";
import CartItem from "./CartItem/CartItem";
const Cart = ( {cart, handleUpdateCartQty, handleRemoveFromCart, handleEmptyCart}) => {
    console.log(cart);
    const classes = useStyles();
    
       
    
    const EmptyCart = () => {

        return (<Typography variant="subtitle1">You have no items in your shoping cart.</Typography>);

    };

    const FilledCart = () => {

        return (<> <Grid constainer spacing={3}>
            {cart.line_items.map((item) => (
                <Grid item xs={12} sm={4} key={item.id}>
                    
                     <CartItem  item={item}/>
                </Grid>
            ))}
        </Grid>
            <div className={classes.cardDetails}>
                <Typography variant="h4">
                    Subtotal: {cart.subtotal.formatted_with_symbol}
                </Typography>
                <div>
                    <Button className={classes.emptyButton} size="large" type="button" variant="contained" color="secondary">Empty Cart</Button>
                    <Button className={classes.checkout} size="large" type="button" variant="contained" color="Primary">Checkout</Button>
                </div>
            </div>
        </>
        )
    }

    if(!cart.line_items) return "Loading...";

    return (<div>
        <Container>
            <div className={classes.toolbar} />
            <Typography className={classes.title} variant="h3" gutterBottom>Your Shoping Cart</Typography>
            {!cart.line_items.length ? <EmptyCart /> : <FilledCart />}
        </Container>


    </div>
    )
};

export default Cart;
